frappe.listview_settings['Purchase Order'] = {
    onload: function(listview) {
        listview.page.add_inner_button(__('Print PR'), function() {
            const selected_docs = listview.get_checked_items();

            if (selected_docs.length === 0) {
                frappe.msgprint({
                    title: __('Warning'),
                    indicator: 'red',
                    message: __('Please select at least one Purchase Order')
                });
                return;
            }

            const po_id = selected_docs[0].name;

            // First get the PO details
            frappe.db.get_doc('Purchase Order', po_id)
                .then(po_doc => {
                    console.log("PO Document:", po_doc);

                    // Call server script
                    frappe.call({
                        method: 'get_linked_purchase_receipts',
                        args: {
                            purchase_order: po_id
                        },
                        callback: function(r) {
                            console.log("Server Response:", r);

                            if (r.message.status === 'error') {
                                frappe.msgprint({
                                    title: __('Error'),
                                    indicator: 'red',
                                    message: r.message.message
                                });
                                return;
                            }

                            const receipts = r.message.message;
                            console.log("Debug Info:", r.message.debug);

                            if (receipts && receipts.length > 0) {
                                showPurchaseReceiptsDialog(receipts, po_id);
                            } else {
                                // Also check child table directly
                                frappe.db.get_list('Purchase Receipt Item', {
                                    filters: {
                                        'purchase_order': po_id
                                    },
                                    fields: ['parent', 'purchase_order', 'name', 'item_code'],
                                    limit: 0
                                }).then(items => {
                                    console.log("Direct Child Table Query:", items);

                                    frappe.msgprint({
                                        title: __('Not Found'),
                                        indicator: 'orange',
                                        message: __('No Purchase Receipts found for Purchase Order: ') + po_id + 
                                                '\nPlease check browser console for debug information.' +
                                                '\nChild Table Records Found: ' + items.length
                                    });
                                });
                            }
                        }
                    });
                })
                .catch(err => {
                    console.error("Error fetching PO:", err);
                    frappe.msgprint({
                        title: __('Error'),
                        indicator: 'red',
                        message: __('Error fetching Purchase Order details. Check console for more information.')
                    });
                });
        });
    }
};

function showPurchaseReceiptsDialog(receipts, po_id) {
    const d = new frappe.ui.Dialog({
        title: __('Purchase Receipts for PO: ') + po_id,
        fields: [
            {
                fieldtype: 'HTML',
                fieldname: 'pr_list'
            }
        ]
    });

    let html = `
        <div class="pr-list">
            <table class="table table-bordered">
                <thead>
                    <tr>
                        <th>Purchase Receipt</th>
                        <th>Date</th>
                        <th>Item Code</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
    `;

    receipts.forEach(pr => {
        html += `
            <tr>
                <td>${pr.name}</td>
                <td>${frappe.datetime.str_to_user(pr.posting_date)}</td>
                <td>${pr.item_code || ''}</td>
                <td>
                    <button class="btn btn-xs btn-default" 
                            onclick="frappe.set_route('Form', 'Purchase Receipt', '${pr.name}')">
                        View
                    </button>
                </td>
            </tr>
        `;
    });

    html += `
                </tbody>
            </table>
        </div>
    `;

    d.fields_dict.pr_list.$wrapper.html(html);
    d.show();
}

frappe.listview_settings['Purchase Order'] = {
    onload: function(listview) {
        // Add custom button to list view
        listview.page.add_inner_button(__('Merge'), function() {
            // Get selected documents
            const selected_docs = listview.get_checked_items();

            // Check if any documents are selected
            if (selected_docs.length === 0) {
                frappe.msgprint({
                    title: __('Warning'),
                    indicator: 'red',
                    message: __('Please select at least one Purchase Receipt')
                });
                return;
            }

            // Get details of each selected Purchase Receipt
            Promise.all(selected_docs.map(doc => {
                return new Promise((resolve) => {
                    frappe.db.get_doc('Purchase Invoice', doc.name)
                        .then(pr_doc => {
                            resolve(pr_doc);
                        });
                });
            })).then(receipts => {
                if (receipts.length > 0) {
                    // Get first document's vendor and supplier for comparison
                    const firstDoc = receipts[0];
                    const firstVendor = firstDoc.custom_vendor;
                    const firstSupplier = firstDoc.supplier;

                    // Check if all documents have the same vendor and supplier
                    const hasDifferentValues = receipts.some(doc =>
                        doc.custom_vendor !== firstVendor ||
                        doc.supplier !== firstSupplier
                    );

                    if (hasDifferentValues) {
                        frappe.msgprint({
                            title: __('Cannot Merge'),
                            indicator: 'red',
                            message: __('Selected receipts have different vendor and supplier values. Please select receipts with matching vendor and supplier.')
                        });
                    } else {
                        // If vendors match, format item details
                        let message = `Matching Purchase Receipts (Vendor: ${firstVendor}, Supplier: ${firstSupplier})\n\n`;

                        receipts.forEach(receipt => {
                            message += `Purchase Receipt: ${receipt.name}\n`;
                            message += `${'='.repeat(100)}\n`;
                            message += `Item Code | Item Name | Qty | UOM | Rate | Amount | Warehouse\n`;
                            message += `${'-'.repeat(100)}\n`;

                            receipt.items.forEach(item => {
                                message += `${item.item_code} | `;
                                message += `${item.item_name || 'N/A'} | `;
                                message += `${item.qty} | `;
                                message += `${item.uom} | `;
                                message += `${item.rate} | `;
                                message += `${item.amount} | `;
                                message += `${item.warehouse}\n`;
                            });
                            message += `\n`;

                            // Add receipt totals
                            message += `Total Quantity: ${receipt.items.reduce((sum, item) => sum + item.qty, 0)}\n`;
                            message += `Total Amount: ${receipt.total}\n\n`;
                        });

                        // Display results in a dialog box
                        frappe.msgprint({
                            title: __('Items in Selected Purchase Receipts'),
                            indicator: 'green',
                            message: message,
                            wide: true
                        });

                        // Log for debugging
                        console.log("Purchase Receipts Data:", receipts);
                    }
                }
            });
        }, 'Actions');
    },

    // Add checkbox selection to the list view
    onrender: function(listview) {
        // Ensure checkboxes are visible
        listview.page.wrapper.find('.list-row-checkbox').removeClass('hide');
    }
};

