frappe.ui.form.on('Purchase Order', {
    refresh: function (frm) {
        // This function will run when the form is refreshed
        // You can initialize or manipulate fields here if needed
    },
    onload: function (frm) {
        // This function will run when the form is loaded
        // You can initialize fields or set default values here if needed
        let supplier = frm.doc.supplier;
        if (supplier) {
            // Get the first 3 characters of the supplier_code
            let codeSubstring = supplier.substring(0, 3).toUpperCase();

            // Set the substring to another field or use it as needed
            frm.set_value('custom_supplier_code', codeSubstring);
        } else {
            // Clear the substring field if supplier_code is empty
            frm.set_value('custom_supplier_code', '');
        }
    },
    supplier: function (frm) {
        // This function triggers when `supplier_code` field is modified
        let supplier = frm.doc.supplier;
        if (supplier) {
            // Get the first 3 characters of the supplier_code
            let codeSubstring = supplier.substring(0, 3).toUpperCase();

            // Set the substring to another field or use it as needed
            frm.set_value('custom_supplier_code', codeSubstring);
        } else {
            // Clear the substring field if supplier_code is empty
            frm.set_value('custom_supplier_code', '');
        }
    }
});


frappe.ui.form.on('Purchase Order', {
    refresh: function (frm) {
        // This function will run when the form is refreshed
        // You can initialize or manipulate fields here if needed
    },
    onload: function (frm) {
            let transaction_date = frm.doc.transaction_date;

            let date = new Date(transaction_date);
            let days = ['SUN', 'MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT'];
            // Get the day of the week
            let dayOfWeek = days[date.getDay()];


            let fullYear = date.getFullYear();
            // Extract the last two digits of the year
            let yearYY = fullYear.toString().slice(-2);
            let order_code = "POA"+dayOfWeek+yearYY;
            if(frm.doc.docstatus === 0) {
                frm.set_value('custom_order_code', order_code);    
            }


    },
    transaction_date: function (frm) {
        let transaction_date = frm.doc.transaction_date;
            let date = new Date(transaction_date);
            let days = ['SUN', 'MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT'];
            // Get the day of the week
            let dayOfWeek = days[date.getDay()];
            let fullYear = date.getFullYear();
            // Extract the last two digits of the year
            let yearYY = fullYear.toString().slice(-2);
            let order_code = "POA"+dayOfWeek+yearYY;
            if(frm.doc.docstatus === 0) {
                frm.set_value('custom_order_code', order_code);
            }

    }
});

