function calculate_and_set_gst_value(frm) {
    // console.log(frm.doc.items)
    $.each(frm.doc.items || [], function (i, item) {
        let total_sgst = 0;
        let total_cgst = 0;
        let total_igst = 0;
        // console.log(item)
        // if (item.taxes) {
        //     $.each(item.taxes || [], function (j, tax) {
        //         // console.log(item)
        //         if (tax.account_head) {
        //             if (tax.account_head.includes('SGST')) {
        //                 total_sgst += flt(tax.tax_amount);
        //             } else if (tax.account_head.includes('CGST')) {
        //                 total_cgst += flt(tax.tax_amount);
        //             } else if (tax.account_head.includes('IGST')) {
        //                 total_igst += flt(tax.tax_amount);
        //             }
        //         }
        //     });
        //     // console.log(item)

        // }

        if (item.sgst_amount == null) { // This checks for both null and undefined
            item.sgst_amount = 0;
        }

        if (item.cgst_amount == null) { // This checks for both null and undefined
            item.cgst_amount = 0;
        }

        if (item.igst_amount == null) { // This checks for both null and undefined
            item.igst_amount = 0;
        }

        if (item.net_amount == null) { // This checks for both null and undefined
            item.net_amount = 0;
        }

        let total_tax = 0;
        let grand_total = 0;
        // frappe.msgprint(item.sgst_amount +" => "+ item.cgst_amount+"=>" + item.igst_amount)
        total_tax = parseFloat(item.sgst_amount) + parseFloat(item.cgst_amount) + parseFloat(item.igst_amount);

        // console.log( "total taxt"+total_tax)
        grand_total = parseFloat(item.net_amount) + parseFloat(total_tax);
        console.log(grand_total);
        // console.log("grand total " + grand_total)
        // console.log("herere")
        // console.log(gst_value);
        //Set the GST value in the custom field of the item
        frappe.model.set_value(item.doctype, item.name, 'custom_gstn_value', total_tax);
        frappe.model.set_value(item.doctype, item.name, 'custom_grand_total', grand_total);
        frm.refresh_field('items');

    });
}
