    },
    onload: function (frm) {
        if (!frm.doc.fiscal_year) {
            // Fetch the current fiscal year based on the current date
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Fiscal Year',
                    filters: {
                        'year_start_date': ['<=', frappe.datetime.get_today()],
                        'year_end_date': ['>=', frappe.datetime.get_today()]
                    },
                    fields: ['name']
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        var fiscal_year = r.message[0].name;
                        var start_year = fiscal_year.split('-')[0];
                        var end_year = fiscal_year.split('-')[1];
                        display_fiscal_year = start_year.substring(2) + '-' + end_year.substring(2);    
                        console.log("==>" + fiscal_year)
                        frm.set_value('custom_fiscal_year', display_fiscal_year);
                    }
                }
            });
        }
    }
});

