function calculate_freight_and_forwarding(frm) {
    // console.log("here");
    // Add your calculation logic here
    let custom_weight_in_kg = frm.doc.custom_weight_in_kg || 0;
    let custom_rate_per_kg = frm.doc.custom_rate_per_kg || 0;
    let total_freight_charges = custom_weight_in_kg * custom_rate_per_kg;
    let freight_cnt = 0;
    // console.log(frm.doc)
    if (frm.doc.taxes) {
        $.each(frm.doc.taxes || [], function (j, tax) {
            if (tax.account_head) {

                if (tax.account_head.includes('Freight and Forwarding Charges') && freight_cnt == 0) {
                    // console.log("here"+ tax.account_head + "  =>" + freight_cnt);
                    frappe.model.set_value(tax.doctype, tax.name, 'tax_amount', total_freight_charges);
                    frm.refresh_field('purchase_taxes_and_charges');
                    freight_cnt++;
                } 
            }
        });

        // return false;
        // console.log(item)

    }
}

