        // You can initialize or manipulate fields here if needed
    },
    onload: function (frm) {
        // This function will run when the form is loaded
        // You can initialize fields or set default values here if needed
        let custom_vendor = frm.doc.custom_vendor;
        if (custom_vendor) {
            // Get the first 3 characters of the supplier_code
            let codeSubstring = custom_vendor.substring(0, 5).toUpperCase();

            // Set the substring to another field or use it as needed
            frm.set_value('custom_vendor_code', codeSubstring);
        } else {
            // Clear the substring field if supplier_code is empty
            frm.set_value('custom_vendor_code', '');
        }
    },
    custom_vendor: function (frm) {
        // This function triggers when `supplier_code` field is modified
        let custom_vendor = frm.doc.custom_vendor;
        if (custom_vendor) {
            // Get the first 3 characters of the supplier_code
            let codeSubstring = custom_vendor.substring(0, 5).toUpperCase();

            // Set the substring to another field or use it as needed
            frm.set_value('custom_vendor_code', codeSubstring);
        } else {
            // Clear the substring field if supplier_code is empty
            frm.set_value('custom_vendor_code', '');
        }
    }
});


frappe.ui.form.on('Purchase Invoice', {
    refresh: function (frm) {
        // This function will run when the form is refreshed
        // You can initialize or manipulate fields here if needed
    },
    onload: function (frm) {
        if (!frm.doc.fiscal_year) {
            // Fetch the current fiscal year based on the current date
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Fiscal Year',
                    filters: {
                        'year_start_date': ['<=', frappe.datetime.get_today()],
                        'year_end_date': ['>=', frappe.datetime.get_today()]
                    },
                    fields: ['name']
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        var fiscal_year = r.message[0].name;
                        var start_year = fiscal_year.split('-')[0];
                        var end_year = fiscal_year.split('-')[1];
                        display_fiscal_year = start_year.substring(2) + '-' + end_year.substring(2);    
                        console.log("==>" + fiscal_year)
                        frm.set_value('custom_fiscal_year', display_fiscal_year);
                    }
                }
            });
        }
    }
});

