frappe.listview_settings['Purchase Receipt'] = {
    onload: function(listview) {
        listview.page.add_action_item(__('Create Purchase Invoice'), function() {
            try {
                const selected_docs = listview.get_checked_items();

                if (selected_docs.length === 0) {
                    frappe.msgprint({
                        title: __('Warning'),
                        indicator: 'red',
                        message: __('Please select at least one Purchase Receipt')
                    });
                    return;
                }

                // Get fiscal year first
                frappe.call({
                    method: 'frappe.client.get_list',
                    args: {
                        doctype: 'Fiscal Year',
                        filters: {
                            'year_start_date': ['<=', frappe.datetime.get_today()],
                            'year_end_date': ['>=', frappe.datetime.get_today()]
                        },
                        fields: ['name']
                    },
                    callback: function(fiscal_response) {
                        try {
                            let display_fiscal_year = '';
                            if (fiscal_response.message && fiscal_response.message.length > 0) {
                                let fiscal_year = fiscal_response.message[0].name;
                                let start_year = fiscal_year.split('-')[0];
                                let end_year = fiscal_year.split('-')[1];
                                display_fiscal_year = start_year.substring(2) + '-' + end_year.substring(2);
                            }

                            frappe.call({
                                method: 'frappe.client.get',
                                args: { 
                                    doctype: 'Purchase Receipt', 
                                    name: selected_docs[0].name 
                                },
                                callback: function(firstDocResponse) {
                                    try {
                                        if (!firstDocResponse.message) {
                                            throw new Error('Failed to fetch first Purchase Receipt');
                                        }

                                        const firstDoc = firstDocResponse.message;
                                        const firstVendor = firstDoc.custom_vendor;
                                        const firstSupplier = firstDoc.supplier;
                                        const firstBranch = firstDoc.custom_branch;
                                        const firstTaxCategory = firstDoc.tax_category;
                                        const firstTaxesAndCharges = firstDoc.taxes_and_charges;
                                        const isReturn = firstDoc.is_return || 0;
                                        const vendor_code = firstVendor ? firstVendor.substring(0, 5).toUpperCase() : '';

                                        const naming_series = isReturn ? 
                                            `DN./.${display_fiscal_year}./.###` : 
                                            `${vendor_code}./.${display_fiscal_year}./.###`;

                                        // Validate fields
                                        if (!firstTaxCategory || !firstTaxesAndCharges) {
                                            frappe.msgprint({
                                                title: __('Error'),
                                                indicator: 'red',
                                                message: __('Tax Category and Taxes and Charges must be set for the first selected receipt.')
                                            });
                                            return;
                                        }

                                        if (!firstBranch) {
                                            frappe.msgprint({
                                                title: __('Error'),
                                                indicator: 'red',
                                                message: __('Branch is not set for the first selected receipt.')
                                            });
                                            return;
                                        }

                                        // Fetch tax template
                                        frappe.call({
                                            method: 'frappe.client.get',
                                            args: {
                                                doctype: 'Purchase Taxes and Charges Template',
                                                name: firstTaxesAndCharges
                                            },
                                            callback: function(tax_template_response) {
                                                try {
                                                    if (!tax_template_response.message) {
                                                        throw new Error('Failed to fetch tax template');
                                                    }

                                                    const tax_template = tax_template_response.message;
                                                    let consolidatedItems = [];
                                                    let totalGrandTotal = 0;
                                                    let totalFreightAmount = 0;

                                                    // Process all selected receipts
                                                    const promises = selected_docs.map(doc => 
                                                        new Promise((resolve, reject) => {
                                                            frappe.call({
                                                                method: 'frappe.client.get',
                                                                args: { 
                                                                    doctype: 'Purchase Receipt', 
                                                                    name: doc.name 
                                                                },
                                                                callback: function(r) {
                                                                    if (r.message) {
                                                                        resolve(r.message);
                                                                    } else {
                                                                        reject('Failed to fetch receipt');
                                                                    }
                                                                }
                                                            });
                                                        })
                                                    );

                                                    Promise.all(promises)
                                                        .then(receipts => {
                                                            // Consolidate items
                                                            receipts.forEach(receipt => {
                                                                totalGrandTotal += flt(receipt.custom_grand_total || 0);
                                                                totalFreightAmount += flt(receipt.custom_freight_amount || 0);

                                                                receipt.items.forEach(item => {
                                                                    const existingItem = consolidatedItems.find(
                                                                        ci => ci.item_name.toLowerCase() === item.item_name.toLowerCase() && 
                                                                             ci.custom_gstn_value === item.custom_gstn_value
                                                                    );

                                                                    if (existingItem) {
                                                                        const newQty = flt(existingItem.qty) + flt(item.qty);
                                                                        const newAmount = flt(existingItem.amount) + flt(item.amount);

                                                                        existingItem.qty = newQty;
                                                                        existingItem.amount = newAmount;
                                                                        existingItem.rate = flt(newAmount / newQty);
                                                                        existingItem.custom_grand_total = flt(existingItem.custom_grand_total || 0) + flt(item.custom_grand_total || 0);
                                                                    } else {
                                                                        consolidatedItems.push({
                                                                            item_code: item.item_code,
                                                                            item_name: item.item_name,
                                                                            qty: flt(item.qty),
                                                                            rate: flt(item.rate),
                                                                            amount: flt(item.amount),
                                                                            warehouse: item.warehouse,
                                                                            custom_grand_total: flt(item.custom_grand_total || 0),
                                                                            custom_gstn_value: item.custom_gstn_value
                                                                        });
                                                                    }
                                                                });
                                                            });

                                                            // Prepare taxes array from template
                                                            let taxes = [];
                                                            if (tax_template.taxes) {
                                                                taxes = tax_template.taxes.map((tax, idx) => {
                                                                    let row_id = null;
                                                                    if (tax.charge_type === 'On Previous Row Amount' || tax.charge_type === 'On Previous Row Total') {
                                                                        if (tax.row_id && parseInt(tax.row_id) < (idx + 1)) {
                                                                            row_id = tax.row_id;
                                                                        } else {
                                                                            frappe.throw(__(`Invalid row reference in tax row ${idx + 1}`));
                                                                        }
                                                                    }

                                                                    return {
                                                                        account_head: tax.account_head,
                                                                        charge_type: tax.charge_type,
                                                                        row_id: row_id,
                                                                        description: tax.description,
                                                                        included_in_print_rate: tax.included_in_print_rate || 0,
                                                                        included_in_paid_amount: tax.included_in_paid_amount || 0,
                                                                        rate: tax.rate,
                                                                        account_currency: tax.account_currency,
                                                                        cost_center: tax.cost_center
                                                                    };
                                                                });
                                                            }

                                                            // Create Purchase Invoice
                                                            const doc = {
                                                                doctype: 'Purchase Invoice',
                                                                naming_series: naming_series,
                                                                custom_fiscal_year: display_fiscal_year,
                                                                custom_vendor_code: vendor_code,
                                                                supplier: firstSupplier,
                                                                custom_vendor: firstVendor,
                                                                custom_branch: firstBranch,
                                                                tax_category: firstTaxCategory,
                                                                taxes_and_charges: firstTaxesAndCharges,
                                                                taxes: taxes,
                                                                items: consolidatedItems,
                                                                custom_grand_total: totalGrandTotal,
                                                                custom_freight_amount: totalFreightAmount,
                                                                update_stock: 1,
                                                                is_return: isReturn
                                                            };

                                                            console.log('Creating Purchase Invoice with:', doc);

                                                            frappe.call({
                                                                method: 'frappe.client.insert',
                                                                args: { doc: doc },
                                                                callback: function(response) {
                                                                    if (response.message) {
                                                                        frappe.msgprint({
                                                                            title: __('Success'),
                                                                            indicator: 'green',
                                                                            message: __('Purchase Invoice created successfully: ') + response.message.name
                                                                        });
                                                                    } else {
                                                                        frappe.msgprint({
                                                                            title: __('Error'),
                                                                            indicator: 'red',
                                                                            message: __('Failed to create Purchase Invoice')
                                                                        });
                                                                    }
                                                                }
                                                            });
                                                        })
                                                        .catch(error => {
                                                            console.error('Error processing receipts:', error);
                                                            frappe.msgprint({
                                                                title: __('Error'),
                                                                indicator: 'red',
                                                                message: __('Error processing receipts: ') + error
                                                            });
                                                        });
                                                } catch (error) {
                                                    console.error('Error in tax template processing:', error);
                                                    frappe.msgprint({
                                                        title: __('Error'),
                                                        indicator: 'red',
                                                        message: __('Error in tax template processing: ') + error.message
                                                    });
                                                }
                                            }
                                        });
                                    } catch (error) {
                                        console.error('Error processing first document:', error);
                                        frappe.msgprint({
                                            title: __('Error'),
                                            indicator: 'red',
                                            message: __('Error processing first document: ') + error.message
                                        });
                                    }
                                }
                            });
                        } catch (error) {
                            console.error('Error in fiscal year processing:', error);
                            frappe.msgprint({
                                title: __('Error'),
                                indicator: 'red',
                                message: __('Error in fiscal year processing: ') + error.message
                            });
                        }
                    }
                });
            } catch (error) {
                console.error('Error in main function:', error);
                frappe.msgprint({
                    title: __('Error'),
                    indicator: 'red',
                    message: __('Error in main function: ') + error.message
                });
            }
        });
    },

    onrender: function(listview) {
        listview.page.wrapper.find('.list-row-checkbox').removeClass('hide');
    }
};

