                                                                supplier_address: supplier_address,
                                                                shipping_address: shipping_address,
                                                                billing_address: billing_address,
                                                                custom_vendor_address: vendor_address
                                                            };

                                                            console.log('Creating Purchase Invoice with:', doc);

                                                            frappe.call({
                                                                method: 'frappe.client.insert',
                                                                args: { doc: doc },
                                                                callback: function(response) {
                                                                    if (response.message) {
                                                                        frappe.msgprint({
                                                                            title: __('Success'),
                                                                            indicator: 'green',
                                                                            message: __('Purchase Invoice created successfully: ') + response.message.name
                                                                        });
                                                                    } else {
                                                                        frappe.msgprint({
                                                                            title: __('Error'),
                                                                            indicator: 'red',
                                                                            message: __('Failed to create Purchase Invoice')
                                                                        });
                                                                    }
                                                                }
                                                            });
                                                        })
                                                        .catch(error => {
                                                            console.error('Error processing receipts:', error);
                                                            frappe.msgprint({
                                                                title: __('Error'),
                                                                indicator: 'red',
                                                                message: __('Error processing receipts: ') + error
                                                            });
                                                        });
                                                } catch (error) {
                                                    console.error('Error in tax template processing:', error);
                                                    frappe.msgprint({
                                                        title: __('Error'),
                                                        indicator: 'red',
                                                        message: __('Error in tax template processing: ') + error.message
                                                    });
                                                }
                                            }
                                        });
                                    } catch (error) {
                                        console.error('Error processing first document:', error);
                                        frappe.msgprint({
                                            title: __('Error'),
                                            indicator: 'red',
                                            message: __('Error processing first document: ') + error.message
                                        });
                                    }
                                }
                            });
                        } catch (error) {
                            console.error('Error in fiscal year processing:', error);
                            frappe.msgprint({
                                title: __('Error'),
                                indicator: 'red',
                                message: __('Error in fiscal year processing: ') + error.message
                            });
                        }
                    }
                });
            } catch (error) {
                console.error('Error in main function:', error);
                frappe.msgprint({
                    title: __('Error'),
                    indicator: 'red',
                    message: __('Error in main function: ') + error.message
                });
            }
        });
    },

    onrender: function(listview) {
        listview.page.wrapper.find('.list-row-checkbox').removeClass('hide');
    }
};
